import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "./components/Header/Header";
import './App.css';
import Movies from "./components/Movies/Movies";

export default function App() {
 const [movie, setMovie] = useState([]);
 const [page, setPage] = useState(1);
 const [searchQuery, setSearchQuery] = useState("Rumble Through the Dark");

 const fetchData = async () => {
    const options = {
      method: 'GET',
      url: 'https://api.themoviedb.org/3/discover/movie',
      params: {
        include_adult: 'false',
        include_video: 'false',
        page: page,
        sort_by: 'popularity.desc',
        q: searchQuery,
      },
      headers: {
        accept: 'application/json',
        Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIwZWFmM2ZjM2RiMWQwOThiYmVmOWE1NGMxZTM5NGUxZiIsInN1YiI6IjY1OGM3YjA3N2ExYmQ2MmQ3MjU0YjBhZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.S8FUYVDzcxjD4o7cCYpU2Cxuf0EMNo8xec4oPnfFI6E'
      }
    };

    try {
      const response = await axios.request(options);
      setMovie(response.data.results);
    } catch (error) {
      console.error("Error fetching data", error);
    }
 };

 const goBack = () => {
    setPage(page - 1);
 };

 const goForward = () => {
    setPage(page + 1);
 };

 useEffect(() => {
    fetchData();
 }, [page, searchQuery]);

 return (
    <div className="home">
      <Header searchQuery={searchQuery} setSearchQuery={setSearchQuery} onSearch={fetchData} />
      <div className="list">
        {movie.map((item) => (
          <Movies key={item.id} data={item} />
        ))}
      </div>
      <div className="page">
        <i className='bx bx-chevron-left' onClick={goBack}></i>
        <i className='bx bx-chevron-right' onClick={goForward}></i>
      </div>
    </div>
 );
}
